  gf_point(TenYearCHD ~ age)  |&gt;
  gf_hline(yintercept = c(0,1), lty = 2) |&gt; 
  gf_labs(y = "CHD Risk", x = "Age") |&gt; 
  gf_refine(stat_smooth(method ="glm", method.args = list(family = "binomial"), 
